import React, { useContext, useRef } from 'react'
import { todoContext } from '../context/todoContext'


export const AddTodo = () => {
    const { todos, editTodos } = useContext(todoContext)
    const todoName = useRef()
    const todowork = useRef()
    function addTodo(e) {
        e.preventDefault()
        const newTodo = todoInput.current.value;
        editTodos([...todos, newTodo])

    }
    return (<React.Fragment>
        <div className='container text-center p-3'>
            <h2 className=' m-4'>Write down the things you want to do....</h2>
            <form className=''>
                <input type="text" ref={todoName} placeholder='Todo Name...' id='addTodo' className='form-control' />
                <input type="text" ref={todowork} placeholder='work you have to do...' className='form-control' />
                <input type="date" name="" id="" />
                <input type="submit" value="Add" onClick={addTodo} className='btn form-button' />

            </form>
            <div>{todos}</div>
        </div>

    </React.Fragment>)
}
