import React, { useContext, useRef } from 'react'
import { todoContext } from '../context/todoContext'


export const AddTodo = () => {
    const { todos, editTodos } = useContext(todoContext)
    const todoName = useRef()
    const todowork = useRef()
    const todoDate = useRef()
    const addButton = useRef()
    function addTodo(e) {
        e.preventDefault()
        //        const newTodo = todoInput.current.value;
        //        editTodos([...todos, newTodo])

    }

    function formchek(){
        if(todoDate.current.value !== ''&&todowork.current.value !== ''&&todoName.current.value !== '')
            addButton.current.disabled = false
        else
            addButton.current.disabled = true
    }
    return (<React.Fragment>
        <div className='container text-center p-3'>
            <h2 className=' m-4'>Write down the things you want to do....</h2>
            <form className=''>
                <div className='row col-12'>
                    <input type="text" ref={todoName} onInput={formchek} placeholder='Todo Name...' id='todoName' className='form-control col-6 inline todo-Input' />
                    <input type="date" name="deadLine" ref={todoDate} id="deadLine" onInput={formchek} className='col-5 inline form-control todo-Input' />
                </div>
                <input type="text" ref={todowork} onInput={formchek} placeholder='work you have to do...' className='form-control todo-Input m-3' />
                <input type="submit" disabled value="Add" ref={addButton} onClick={addTodo} className='btn form-button' />
            </form>
            <div>{ }</div>
        </div>

    </React.Fragment>)
}
